from Player import Playerfrom const import *import mathclass ClientAction:	def __init__(self, keyinput, sensing_data=None):		self.keyinput = keyinput		self.direction = 0		self.move_flag = Player.PLAYER_MOVE_STOP		self.sensing_data = sensing_data#	# return theta#	def set_sight_direction(self):#		if sensing_data #		return math.atan2(self.keyinput.mouseY - self.y, self.keyinput.mouseX - self.x)	def move_from_keyboard(self):		key = self.keyinput.pressTime		direction = 0		x = 0		y = 0		if key[KEY_UP] > 0:			y -= 1		elif key[KEY_DOWN] > 0:			y += 1		if key[KEY_RIGHT] > 0:			x += 1		elif key[KEY_LEFT] > 0:			x -= 1		self.direction = math.atan2(y,x)		if key[KEY_RUN] > 0:			self.move_flag = Player.PLAYER_MOVE_RUN		elif key[KEY_SNEAK] > 0:			self.move_flag = Player.PLAYER_MOVE_SNEAK		else:			self.move_flag = Player.PLAYER_MOVE_WALK		if x == 0 and y == 0:			self.move_flag = Player.PLAYER_MOVE_STOP		print(key)	def update(self):		self.move_from_keyboard()		data = {'move_flag': self.move_flag, 'direction': self.direction}		return data